mapper.select:
    select * from res_scheme aa
    
	  
		and aa.id = #{id}
      
	  
		and aa.code = #{code}
      
	  
		and aa.name like CONCAT('%',#{name},'%')
      
	  
		and aa.description like CONCAT('%',#{description},'%') 
      
	  
		and aa.theme like CONCAT('%',#{theme},'%') 
      
	  
		and aa.goal like CONCAT('%',#{goal},'%') 
      
	  
		and aa.begin_time like CONCAT('%',#{beginTime},'%') 
      
	  
		and aa.place like CONCAT('%',#{place},'%') 
      
	  
		and aa.person like CONCAT('%',#{person},'%') 
      
	  
		and aa.form like CONCAT('%',#{form},'%') 
      
	  
		and aa.arrangements like CONCAT('%',#{arrangements},'%') 
      
	  
		and aa.flow like CONCAT('%',#{flow},'%') 
      
	  
		and aa.content like CONCAT('%',#{content},'%') 
      
	  
		and aa.budget = #{budget}
      
	  
		and aa.award like CONCAT('%',#{award},'%') 
      
	  
		and aa.create_time = #{createTime}
      
	  
		and aa.update_time = #{updateTime}
      
	  
		and aa.creator_id = #{creatorId}
      
	  
		and aa.creator_name = #{creatorName}
      
	  
		and aa.department_id = #{departmentId}
      
	  
		and aa.department_name = #{departmentName}
      
      
		and aa.status = #{status}
	  
	  
		and aa.status <> 'DISABLED'
