mapper.select:
    select * from res_student aa


		and aa.id = #{id}


		and aa.name like CONCAT('%',#{name},'%')


		and aa.sex = #{sex}


		and aa.age = #{age}


		and aa.birthday = #{birthday}


		and aa.roots = #{roots}


		and aa.volk = #{volk}


		and aa.code = #{code}


		and aa.id_card = #{idCard}


		and aa.political = #{political}


		and aa.address like CONCAT('%',#{name},'%')


		and aa.tel = #{tel}


		and aa.email = #{email}


		and aa.parent_tel = #{parentTel}


		and aa.parent_name1 = #{parentName1}


		and aa.parent_name2 = #{parentName2}


		and aa.school = #{school}


		and aa.major = #{major}


		and aa.grade = #{grade}


		and aa.clazz = #{clazz}


		and aa.content like CONCAT('%',#{name},'%')


		and aa.profession = #{profession}


    order by create_time desc
